require 'pry'
def consolidate_cart(cart) #is an array
  new_hash = {}

  cart.each do |ele|
    ele.each do |key, hash1|
      if new_hash[key].nil?
        new_hash[key] = hash1
        new_hash[key][:count] = 1
      else
        new_hash[key][:count] += 1
      end
    end
  end
  return new_hash
end

